    private void startVistaPrevia (){
        jFxPanel = new JFXPanel();
        jPanel_PreView_.add(jFxPanel,BorderLayout.CENTER);
        jPanel_PreView_.revalidate();
        jPanel_PreView_.repaint();
        // Creation of scene and future interactions with JFXPanel
        // should take place on the JavaFX Application Thread
        Platform.runLater(() -> {
            webView = new WebView();
            jFxPanel.setScene(new Scene(webView));
            webView.getEngine().loadContent(htmlFinal);
        });
    }

    private void limpiarVistaPrevia() {
        Platform.runLater(() -> {
            webView.getEngine().loadContent("<h1>Sin datos.</h1>");
        });
    }
    
    public void imprimir() {
        try {
            PrinterJob job = PrinterJob.createPrinterJob();
            if (job != null) {
                webView.getEngine().print(job);
                job.endJob();
            }
        } catch (Exception e) {
            System.err.println(e);
        }
    }

    Integer facturaID = gui.venta.Ventas.IDVENTATEMPORAL;

    private void mostrarVentasDetalleNumeroFactura(Integer valor) throws BadLocationException {
String textSize = "75%";
        String imgSize = "10%";
        String img = "<img src=\" data:image/png;base64,\" />"; 
        String head 
                = "<!DOCTYPE html><html lang=\"es\"><head><meta charset=\"UTF-8\"><meta name=\"viewport\" content=\"width=device-width, initial-scale=0.8\"><title>Factura</title><style>## Propiedades@media print {body {-webkit-print-color-adjust: exact;}}## Cuerpohtml {font-family: \"Open Sans\", sans-serif;color: #333;padding: 2rem 0;}html {font-size: "+textSize+";}img {max-width:84%; height:auto; padding:"+imgSize+";}table {width: 100%;}th {padding: 6px; text-align: center;}td {padding: 3px;}td.L {text-align: left;}td.M {text-align: center;}td.R {text-align: right;}#tfoot tr td {font-size: 1.3rem;}## Lineas thead tr:last-child th {border-bottom: 1px solid #bfbfbf;}tbody tr:last-child td {border-bottom: 1px solid #bfbfbf;}tfoot tr:last-child td {border-bottom: double #bfbfbf;}#td.total {border-bottom: double #5f5f5f;}</style></head><body translate=\"no\">"
                + img
                + "<table cellspacing=\"0\" cellpadding=\"0\"><thead><tr><th>Descripción</th><th>Precio</th><th>Cantidad</th><th>Monto</th></tr></thead>"
        ;
        String foot = "</table></body><footer></footer></html>";
        String tBodyH = "<tbody>", tBocyF = "</tbody>", tBodyB = "";
        String tFootH = "<tfoot><tr>", tFootF = "</tr></tfoot>", tFootB = "";
        String tBody = "";
        String tFoot = "";

        Pedidos pedidos = new Pedidos();
        
        String descripcion = null;
        int Cantidad = 0;
        Double Costo = 0.0;
        Double totalconsumido = 0.0;

        try {
            Conexion objCon = new Conexion();
            Connection conn = (Connection) objCon.connect();
            ResultSet rs = null;
            PreparedStatement ps = null;

            String servicios
                    = "SELECT\n"
                    + "     bebida.`nombre` AS bebida_nombre,\n"
                    + "     temp_pedido.`bebida_cantidad` AS temp_pedido_bebida_cantidad,\n"
                    + "     temp_pedido.`costo` AS temp_pedido_costo\n"
                    + "FROM\n"
                    + "     `bebida` bebida INNER JOIN `temp_pedido` temp_pedido ON bebida.`id` = temp_pedido.`bebida_id` where temp_venta_id = '" 
                    + gui.venta.Ventas.IDVENTATEMPORAL  
                    + "'"
            ;
            ps = conn.prepareStatement(servicios);
            rs = ps.executeQuery();
            while (rs.next()) {
                descripcion = (rs.getString("bebida_nombre"));
                Cantidad = (rs.getInt("temp_pedido_bebida_cantidad"));
                Costo = (rs.getDouble("temp_pedido_costo"));
                Double sub = Cantidad * Costo;
                totalconsumido = totalconsumido + sub;
                
                String pedidoX
                        = "<tr><td class=\"L\">"
                        + descripcion //aquí de acuerdo al id crear un query para seleccionar el nombre del producto
                        + "</td><td class=\"R\">Q "
                        + Costo //aquí de acuerdo al id crear un query para seleccionar el precio del producto
                        + "</td><td class=\"M\"> "
                        + Cantidad //las unidades se toman si no son igual a 0
                        + "</td><td class=\"R\">Q "
                        + sub
                        + "</td></tr>"
                ;

                tBodyB = tBodyB + pedidoX;
                
                System.out.println("Datos: " + "desc = " + descripcion + "  cantidad: " + Cantidad + "  costo: " + Costo);
            }
        } catch (SQLException ex) {
            System.out.println(ex.toString());
        }
        
        String totalX 
                = "<td class=\"M\">Total</td><td></td><td></td><td class=\"R total\">Q "
                + totalconsumido
                + "</td></tr>"
        ;

        tFootB = totalX;
        tBody = tBodyH + tBodyB + tBocyF;
        tFoot = tFootH + tFootB + tFootF;

        htmlFinal = head + tBody + tFoot + foot;
        
        Platform.runLater(() -> {
            webView.getEngine().loadContent(htmlFinal);
        });
        
    }

    public Cobrar() {
        initComponents();
        try {
            mostrarVentasDetalleNumeroFactura(gui.venta.Ventas.IDVENTATEMPORAL);
        } catch (BadLocationException ex) {
            Logger.getLogger(Cobrar.class.getName()).log(Level.SEVERE, null, ex);
        }
        startVistaPrevia ();
    }

